parameters.jsCode:
// Calculate Lead Score
let score = 0;
let grade = 'C';

// Budget scoring (40%)
const budget = $input.first().json['What\'s your budget range?'];
if (budget === "$800k+") score += 40;
else if (budget === "$600k - $800k") score += 35;
else if (budget === "$400k - $600k") score += 30;
else if (budget === "$200k - $400k") score += 20;
else score += 10;

// Timeline scoring (30%)
const timeline = $input.first().json['When are you looking to buy/rent?'];
if (timeline.includes("Immediately")) score += 30;
else if (timeline.includes("1-3 months")) score += 25;
else if (timeline.includes("3-6 months")) score += 15;
else score += 5;

// Financing (20%)
if (!budget.includes("financing")) score += 20;
else score += 10;

// Requirements clarity (10%)
if ($input.first().json['Any special requirements or questions?']) score += 10;

// Assign grade
if (score >= 80) grade = 'A';
else if (score >= 60) grade = 'B';

return {
  ...$input.first().json,
  lead_score: score,
  lead_grade: grade
};

// Determine if sender is agent or lead
const agentData = $json.agent_id;
const isAgent = agentData && agentData.length > 0 && $json['agent_phone '];

// Get original message data
const originalData = $json;

if (isAgent) {
  // This is an agent
  const agent = $json;
  return {
    ...originalData,
    sender_type: 'agent',
    agent_name: agent.agent_name,
    agent_id: agent.agent_id,
    agent_location: agent.location_coverage,
    agent_speciality: agent.speciality,
    conversation_context: `Agent ${agent.agent_name} is sending property suggestions`
  };
} else {
  // This is a lead
  return {
    ...originalData,
    sender_type: 'lead',
    conversation_context: 'Lead needs qualification or assistance'
  };
}

const leadData = $('Check if A/B Grade Lead').item.json;
const leadLocation = leadData.location?.toLowerCase() || '';

// Simple hardcoded matching based on your agent data
let matchedAgent;

if (leadLocation.includes('downtown') || leadLocation.includes('central') || leadLocation.includes('riverside')) {
  matchedAgent = {
    'agent_id': 'AGT001',
    'agent_name ': 'Agent Sarah',
    'agent_phone ': 1234567890,
    'location_coverage ': 'downtown,central,riverside',
    'speciality': 'luxury homes'
  };
} else {
  matchedAgent = {
    'agent_id': 'AGT002',
    'agent_name ': 'Agent Mike', 
    'agent_phone ': 1234567891,
    'location_coverage ': 'suburbs,northside,eastside',
    'speciality': 'family homes'
  };
}

const formatPhone = (phone) => '+' + phone.toString();

return [{
  ...leadData,
  agent_phone: formatPhone(matchedAgent['agent_phone ']),
  agent_name: matchedAgent['agent_name '].trim(),
  agent_speciality: matchedAgent.speciality
}];

